public class UpCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Up Casting // Widening // Implicit Casting 
		
		byte a = 10; // -128 to 127 // 1 bytes 
		int b = a;  // 4 bytes 
		
		int c = 1000; // 4 Bytes
		double d = c; // 8 Bytes 
		System.out.println(d);
		
		

	}

}
